import React, { useState } from "react";

export default function App() {
  const [students, setStudents] = useState(null);
  const [filterData, setFilterData ] = useState(null);


  const getStudents=(i)=>{
    fetch('http://localhost:3000/api/students/' +i)
      .then(resp => resp.json())
      .then(data => {
        setFilterData(data.students);
        setStudents(data.students);
      })
  }

  const searchById = (event) => {
    event.persist();
    // Get the search term
    const searchItem = event.target.value.trim();
    console.log('searchItem',searchItem);
    // If search term is empty fill with full students data
    if(!searchItem.trim()) {
      setFilterData(students);
    }
    getStudents(searchItem)
  }

   return (
    <div className="App">
      <h1>Find the Student</h1>

      <div className="search" id="search">
          <input type="number" name="serachById" onChange={(e) => searchById(e)} ></input>
      </div>


      {filterData && filterData.map((student, index) => {
       var total = 0;
       for(var i = 0; i < student.grades.length; i++) {
        var grade = parseInt(student.grades[i]);
        total += grade;
       }
       const avg = total / student.grades.length;
       const average = avg.toString();


      return(
      <div className="student" key={index}>
        <div className="image">
          <img src={student.pic} id="icon"></img>
        </div>

        <div className="text">
          <h3 id="name"> {student.name}</h3>
          <p id="detail"><strong>EMAIL:</strong> {student.email}</p>
          <p id="detail"><strong>COMPANY:</strong> {student.rollno}</p>
          <p id="detail"><strong>SKILL:</strong> {student.address}</p>
          <p id="detail"><strong>AVERAGE:</strong>: {average}%</p>
        </div>
      </div>

      )}
      )}
    </div>
  );
}
