import React from "react";
import * as Yup from "yup";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { FormGroup,FormLabel,FormText, FormControl, Button } from "react-bootstrap";

const StudentForm = (props) => {
  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Rquired"),
    email: Yup.string()
      .email("You have enter an invalid email address")
      .required("Rquired"),
    rollno: Yup.number()
      .positive("Invalid roll number")
      .integer("Invalid roll number")
      .required("Rquired"),
  });
  console.log(props);
  return (
    <div className="form-wrapper">
      <Formik {...props} validationSchema={validationSchema}>
        <Form>
          <FormGroup className="mb-3" controlId="formBasicText">
            <FormLabel>Name</FormLabel>
            <FormControl type="text" placeholder="Enter Name" />
            <FormText className="text-muted">
            </FormText>
          </FormGroup>
          <FormGroup className="mb-3" controlId="formBasicEmail">
            <FormLabel>Email</FormLabel>
            <FormControl type="email" placeholder="Enter email" />
            <FormText className="text-muted">
            </FormText>
          </FormGroup>

          <FormGroup className="mb-3" controlId="formBasicText">
            <FormLabel>Roll No</FormLabel>
            <FormControl type="number" placeholder="Enter roll no." />
            <FormText className="text-muted">
            </FormText>
          </FormGroup>

          <Button variant="danger" size="lg" 
            block="block" type="submit">
            {props.children}
          </Button>
        </Form>
      </Formik>
    </div>
  );
};

export default StudentForm;
