import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { FormGroup,FormLabel,FormText, FormControl, Button } from "react-bootstrap";
import * as Yup from "yup";



export default function SearchStudent () {
  const [students, setStudents] = useState(null);
  const [filterData, setFilterData ] = useState(null);


  function getStudent(id){
      console.log('===========id======',id);
    axios
	.get(
"http://localhost:3000/api/students/" + id)
	.then((res) => {
		if (res) {
            console.log('-----res------',res.data.data);
            return (
            <div className="student">
                <div className="text">
                <h3 id="name">{res.data.data.name}</h3>
                <p id="detail"><strong>EMAIL:</strong> {res.data.data.email}</p>
                <p id="detail"><strong>Roll No:</strong> {res.data.data.rollno}</p>
                <p id="detail"><strong>Address</strong> {res.data.data.address}</p>
                </div>
            </div>
            )
		} else {
            return "No Data Found"
        }
	})
  }

  const searchById = (event) => {

    event.persist();
    const searchItem = event.target.value.trim();
    getStudent(searchItem);
  }

   return (
    <div className="App">
      <h1>Find the Student</h1>


      <div className="search" id="search">
          <input type="text" name="serachById" onChange={(e) => searchById(e)} ></input>
      </div>
    </div>
  );
}
