// CreateStudent Component for add new student

// Import Modules
import React, { useState, useEffect } from "react";
import axios from 'axios';
import StudentForm from "./StudentForm";

// CreateStudent Component
const CreateStudent = () => {
  const [formValues, setFormValues] = 
    useState({ name: '', email: '', rollno: '' })
  // onSubmit handler
  const onSubmit = studentObject => {
    axios.post(
'http://localhost:3000/api/students', 
    studentObject)
      .then(res => {
        console.log('==========res===',res);
        if (res.status === 200)
          alert('Student successfully created')
        else
          Promise.reject()
      })
      .catch(err => alert('Something went wrong'))
  }

  // Return student form
  return(
    <StudentForm initialValues={formValues} 
      onSubmit={onSubmit} 
      enableReinitialize>
      Create Student
    </StudentForm>
  )
}


const SearchStudent = (props) => {
  const [formValues, setFormValues] = 
    useState({ id: ''})
  // onSubmit handler
  const onSubmit = studentObject => {
    axios
    .post(
      "http://localhost:3000/api/students/" + props.match.params.id,
      studentObject)
      .then(res => {
        console.log('==========res===',res);
        if (res.status === 200)
          alert('Student successfully created')
        else
          Promise.reject()
      })
      .catch(err => alert('Something went wrong'))
  }

  // Return student form
  return(
    <StudentForm initialValues={formValues} 
      onSubmit={onSubmit} 
      enableReinitialize>
      Search Student
    </StudentForm>
  )
}

// Export CreateStudent Component
export default {CreateStudent,SearchStudent}
